import React, { useState } from 'react';
import { TabContent, TabPane, Nav, NavItem, NavLink } from 'reactstrap';

function Home() {
  const [active, setActive] = useState(1);

  const toggleTab = (tab) => {
    if (active !== tab) {
      setActive({ active: tab });
    }
  };
  const handleTopChart = () => {
      setActive({active: 1})
  }
  const handleTopArtist = () => {
      setActive({active: 2})
      alert('munculs')
  }
  return (
    <div className="min-h-screen bg-csb-black">
      <div className="bg-csb-red">
        <Nav tabs fill>
          <NavItem>
            <NavLink active={active === 1} onClick={() => {handleTopChart()}}>Top Chart</NavLink>
          </NavItem>
          <NavItem>
            <NavLink active={active === 2} onClick={() => {handleTopArtist()}}>Top Artist</NavLink>
          </NavItem>
        </Nav>
        <TabContent activeTab={active}>
          <TabPane tabId={1}>Top Chart</TabPane>
          <TabPane tabId={2}><p className='text-csb-white'>Top Artist</p></TabPane>
        </TabContent>
      </div>
    </div>
  );
}

export default Home;
