import React, { useState } from 'react';
import { TabContent, TabPane, Nav, NavItem, NavLink } from 'reactstrap';

function Home() {
  const [active, setActive] = useState(1);

  const toggleTab = (tab) => {
    if (active !== tab) {
      setActive({ active: tab });
    }
  };
  return (
    <div className="min-h-screen bg-csb-black">
      <div className="bg-csb-red">
        <Nav tabs fill>
          <NavItem>
            <NavLink active={active === 1}>Top Chart</NavLink>
          </NavItem>
          <NavItem>
            <NavLink active={active === 2}>Top Artist</NavLink>
          </NavItem>
        </Nav>
        <TabContent activeTab={active}>
          <TabPane tabId={1}>Top Chart</TabPane>
        </TabContent>
        <TabContent activeTab={active}>
          <TabPane tabId={2}>Top Artist</TabPane>
        </TabContent>
      </div>
    </div>
  );
}

export default Home;
