import React, { useState } from 'react';
import { TabContent, TabPane, Nav, NavItem, NavLink } from 'reactstrap';
import { MessageSquare, Play, Share } from 'react-feather';

// import useTopChart from '../hooks/useTopChart';

import herobanner from '../assets/image/hanny-naibaho-aWXVxy8BSzc-unsplash.jpeg';

function Home() {
  const [active, setActive] = useState('1');
  //   const topChart = useTopChart({});

  const handleTopChart = () => {
    setActive({ active: 1 });
  };
  const handleTopArtist = () => {
    setActive({ active: 2 });
    alert('munculs');
  };
  return (
    <div className="min-h-screen bg-csb-black">
      <div className="bg-csb-red">
        <Nav tabs fill>
          <NavItem>
            <NavLink
              active={active === '1'}
              onClick={() => {
                setActive('1');
              }}
            >
              Top Chart
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              active={active === '2'}
              onClick={() => {
                setActive('2');
              }}
            >
              Top Artist
            </NavLink>
          </NavItem>
        </Nav>
        <TabContent activeTab={active}>
          <TabPane tabId="1">
            <div className="px-10">
              <div className="relative">
                <img
                  src={herobanner}
                  alt="hero-banner"
                  style={{ height: '600px', width: '100%' }}
                />
                <h1 className="text-csb-white absolute bottom-8 inset-x-0 display-1 font-bold">
                  Music delivered to you
                </h1>
              </div>

              <div className="flex gap-x-5">
                <button className="flex border rounded-lg px-3 items-center">
                  <span>
                    <Play />
                  </span>
                  Play All
                </button>
                <p className="flex mt-3">
                  <span>
                    <Share />
                  </span>{' '}
                  Share
                </p>
                <p className="flex mt-3">
                  <span>
                    <MessageSquare />
                  </span>
                  Comment
                </p>
              </div>
              <div>
                  <h4>Your Top Chart</h4>
                  <div>

                  </div>
              </div>
            </div>
          </TabPane>
          <TabPane tabId="2">
            <p className="text-csb-white">Top Artist</p>
          </TabPane>
        </TabContent>
      </div>
    </div>
  );
}

export default Home;
