import React, { useState } from 'react';
import { Share } from 'react-feather';
import { Link } from 'react-router-dom';

import useTopArtist from '../hooks/useTopArtist';
import useSearchArtists from '../hooks/useSearchArtist';

function TopArtists() {
  const topArtist = useTopArtist({});
  const searchArtist = useSearchArtists({});
  console.log(searchArtist, 'cekkk');
  const [search, setSearch] = useState('');
  const handleChange = (e) => {
    console.log(e.target.value);
    setSearch({ search: e.target.value });
    console.log(search);
    localStorage.setItem('artist', search.search);
  };
  return (
    <div className="bg-csb-black">
      <div className="bg-csb-red flex gap-x-5 justify-center items-center py-2">
        <Link to="/">
          <p className="font-bold mt-2 text-lg">Dashboard</p>
        </Link>
        <p className="font-bold text-csb-white mt-2 text-lg">Top Artists</p>
        <p className="font-bold mt-2 text-lg">Top Tracks</p>
        <p className="font-bold mt-2 text-lg">Recommendations</p>
        <div>
          <input
            type="text"
            className="px-3 py-2 rounded"
            placeholder="Search Artist"
            onChange={handleChange}
          />
        </div>
      </div>
      <div className="py-10 px-10">
        <h4 className="text-csb-white pb-3">Top Artist</h4>
        {searchArtist.length > 1 ? (
          <div className="flex gap-5 flex-wrap justify-center">
            {searchArtist.isSuccess
              ? searchArtist.data.map((col) => {
                  return (
                    <div
                      style={{ width: '250px', height: '350px' }}
                      className="border p-3 flex flex-col gap-y-3 rounded-lg"
                    >
                      <img
                        src={col.image[2]['#text']}
                        alt="artist"
                        className="mx-auto"
                      />
                      <p className="text-csb-white font-bold whitespace-nowrap">
                        {col.name}
                      </p>
                      <p className="text-csb-white whitespace-nowrap">
                        Total Streams: {col.playcount}
                      </p>
                      <div className="flex gap-x-3">
                        <Share className="text-csb-white" />{' '}
                        <a href={col.url}>Share</a>{' '}
                      </div>
                    </div>
                  );
                })
              : null}
          </div>
        ) : (
          <div className="flex gap-5 flex-wrap justify-center">
            {topArtist.isSuccess
              ? topArtist.data.map((col) => {
                  return (
                    <div
                      style={{ width: '250px', height: '350px' }}
                      className="border p-3 flex flex-col gap-y-3 rounded-lg"
                    >
                      <img
                        src={col.image[2]['#text']}
                        alt="artist"
                        className="mx-auto"
                      />
                      <p className="text-csb-white font-bold whitespace-nowrap">
                        {col.name}
                      </p>
                      <p className="text-csb-white whitespace-nowrap">
                        Total Streams: {col.playcount}
                      </p>
                      <div className="flex gap-x-3">
                        <Share className="text-csb-white" />{' '}
                        <a href={col.url}>Share</a>{' '}
                      </div>
                    </div>
                  );
                })
              : null}
          </div>
        )}
      </div>
    </div>
  );
}

export default TopArtists;
